SELECT

FROM am_action
WHERE
	1=1

AND
	id = #{id}


AND
	action_name LIKE CONCAT('%',#{actionName},'%')


AND
	module_id = #{moduleId}


	AND
	project_id = #{projectId}


	AND
	dep_id = #{depId}


AND
	create_user = #{createUser}

ORDER BY update_time DESC
